import Note from "./components/Note"
import { useState, useEffect } from "react"
import axios from 'axios'

const App = (/* props */) => {
  const [notes, setNotes] = useState([])
  //console.log(notes)
  const [newNote, setNewNote] = useState(
    'a new note...'
  )
  const [showAll, setShowAll] = useState(true)

  /*  useEffect(() => {
     console.log('effect')
     axios
       .get('http://localhost:3001/notes')
       .then(response => {
         console.log('promise fulfilled')
         setNotes(response.data)
       })
   }, []) */

  const toggleImportanceOf = (id) => {
    console.log('importance of ' + id + ' needs to be toggled')
  }

  const hook = () => {
    console.log('effect')
    axios
      .get('http://localhost:3001/notes')
      .then(response => {
        console.log('promise fulfilled')
        setNotes(response.data)
      })
  }
  useEffect(hook, [])
  /*useEffect takes two parameters: 
  1. effect itself - by default, effects run after every completed render, but you can choose 
  to fire it only when certain values have changed
  2. specifies how often the effect is run. - empty [] , then teh effect is only run along with 
  the first render of the component.
  */
  console.log('render', notes.length, 'notes')

  const handleNoteChange = (event) => {
    console.log(event.target.value)
    setNewNote(event.target.value)
  }

  //const { notes } = props
  const addNote = (event) => {
    event.preventDefault()
    const noteObject = {
      content: newNote,
      important: Math.random() < 0.5,
      id: notes.length + 1,
    }
    setNotes(notes.concat(noteObject))
    setNewNote('')
    console.log('button clicked', event.target)

    axios
      .post('http://localhost:3001/notes', noteObject)
      .then(response => {
        setNewNote(notes.concat(response.data))
        setNewNote('')
        console.log(response)
      })
  }

  const notesToShow = showAll
    ? notes
    : notes.filter(note => note.important /* === true */)

  return (
    <div>
      <h1>Notes</h1>
      <div>
        <button onClick={() => setShowAll(!showAll)}>
          show {showAll ? 'important' : 'all'}
        </button>
      </div>
      <ul>
        {notesToShow.map(note =>
          <Note key={note.id} note={note} />)}
        {/*       {notes.map(note => <Note key={note.id} note={note} />)} */}

      </ul>
      <form onSubmit={addNote}>
        <input value={newNote} onChange={handleNoteChange} />
        <button type="submit">save</button>
      </form>
    </div>
  )
}

export default App
